import React, { useState } from "react";

export default function PlaylistPage() {
  const [playlist, setPlaylist] = useState([
    { id: 1, title: "Shape of You", artist: "Ed Sheeran" },
    { id: 2, title: "Happier", artist: "Marshmello" },
  ]);

  const [newSong, setNewSong] = useState("");

  const addSong = () => {
    if (newSong.trim() === "") return;
    setPlaylist([
      ...playlist,
      { id: playlist.length + 1, title: newSong, artist: "Unknown" },
    ]);
    setNewSong("");
  };

  const removeSong = (id) => {
    setPlaylist(playlist.filter((song) => song.id !== id));
  };

  return (
    <div className="max-w-lg mx-auto mt-10 bg-neutral-800 p-6 rounded-xl shadow-md">
      <h2 className="text-2xl font-semibold mb-4">ðŸŽµ Playlist Kamu</h2>

      <div className="flex gap-2 mb-4">
        <input
          type="text"
          value={newSong}
          onChange={(e) => setNewSong(e.target.value)}
          placeholder="Tambahkan lagu baru..."
          className="flex-1 p-2 rounded bg-neutral-700 border border-neutral-600 focus:outline-none"
        />
        <button
          onClick={addSong}
          className="bg-green-500 px-4 py-2 rounded hover:bg-green-600"
        >
          Tambah
        </button>
      </div>

      <ul className="space-y-2">
        {playlist.map((song) => (
          <li
            key={song.id}
            className="flex justify-between items-center bg-neutral-700 px-4 py-2 rounded"
          >
            <span>
              <strong>{song.title}</strong> â€“ {song.artist}
            </span>
            <button
              onClick={() => removeSong(song.id)}
              className="text-red-400 hover:text-red-500"
            >
              âœ•
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
