import React, { useState } from 'react';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const songs = [
    "Perfect - Ed Sheeran",
    "Happier - Marshmello",
    "Stay - Justin Bieber",
    "Shape of You - Ed Sheeran",
    "Let Her Go - Passenger",
    "Deco*27 - Monitoring",
  ];

  const handleSearch = () => {
    const filtered = songs.filter((song) =>
      song.toLowerCase().includes(query.toLowerCase())
    );
    setResults(filtered);
  };

  return (
    <div className="min-h-screen flex flex-col bg-neutral-900 text-white">
      {/* Header */}
      <header className="bg-green-500 p-4 flex justify-between items-center">
        <h1 className="text-xl font-bold">ðŸŽ§ Suify</h1>
        <nav className="space-x-4">
          <a href="#" className="hover:underline">Beranda</a>
          <a href="#" className="hover:underline">Pencarian</a>
          <a href="#" className="hover:underline">Playlist</a>
          <a href="#" className="hover:underline">Profil</a>
        </nav>
      </header>

      {/* Main Content */}
      <main className="flex-1 p-6">
        {/* Hero Section */}
        <section className="text-center my-10">
          <h2 className="text-3xl font-bold mb-2">Selamat Datang di Suify</h2>
          <p className="text-gray-300 mb-6">
            Dengarkan musik favoritmu kapan pun dan di mana pun ðŸŽ¶
          </p>
          <button
            onClick={() => document.getElementById('player').play()}
            className="bg-green-500 hover:bg-green-600 px-6 py-2 rounded-full font-semibold"
          >
            Putar Lagu Contoh
          </button>
        </section>

        {/* Search Section */}
        <section className="max-w-md mx-auto bg-neutral-800 p-6 rounded-xl shadow-md">
          <h3 className="text-xl font-semibold mb-4">Pencarian Lagu</h3>
          <div className="flex gap-2 mb-4">
            <input
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="Cari lagu atau artis..."
              className="flex-1 p-2 rounded bg-neutral-700 border border-neutral-600 focus:outline-none"
            />
            <button
              onClick={handleSearch}
              className="bg-green-500 px-4 py-2 rounded hover:bg-green-600"
            >
              Cari
            </button>
          </div>
          <ul className="list-disc pl-5 space-y-1">
            {results.length > 0 ? (
              results.map((song, i) => <li key={i}>{song}</li>)
            ) : (
              <li className="text-gray-400">Masukkan kata kunci lagu.</li>
            )}
          </ul>
        </section>
      </main>

      {/* Footer / Player */}
      <footer className="bg-neutral-800 p-4 text-center">
        <audio id="player" controls className="w-full">
          <source src="https://www.w3schools.com/html/horse.mp3" type="audio/mpeg" />
          Browser kamu tidak mendukung audio tag.
        </audio>
      </footer>
    </div>
  );
}

export default App;
